// SPDX-License-Identifier: MIT
pragma solidity =0.7.6;

/**
    Base contract for the tokens in the system

    All tokens are burnable and have an operator
 */
import "@openzeppelin/contracts/token/ERC20/ERC20Burnable.sol";

import "../access/OperatorAccessControl.sol";

/**
    Interface
 */
interface IBaseToken {
    function mint(address recipient, uint256 amount) external;

    function burnFrom(address from, uint256 amount) external;
}

/**
    Base implementation of a ERC20 burnable token with access control for an Operator
 */
contract BaseToken is ERC20Burnable, OperatorAccessControl {
    /* ========== CONSTRUCTOR ========== */
    constructor(string memory name_, string memory symbol_) ERC20(name_, symbol_) {}

    /* ========== MUTABLES ========== */
    function mint(address recipient_, uint256 amount_) external onlyOperator {
        _mint(recipient_, amount_);
    }

    function burn(uint256 amount) public override onlyOperator {
        super.burn(amount);
    }

    function burnFrom(address account, uint256 amount) public override onlyOperator {
        super.burnFrom(account, amount);
    }
}

contracts/utils/EpochCounter.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity =0.7.6;

/**
    Counts epoch periods and defines a modifier to prevent operations if the next
    epoch has not been reached yet

    @dev Calling the checkEpoch modifier will automatilly increase the epoch to the
    next period if the check succeeds
 */
import "@openzeppelin/contracts/math/SafeMath.sol";

import "../access/OperatorAccessControl.sol";
import "./StartTimeLock.sol";

contract EpochCounter is OperatorAccessControl, StartTimeLock {
    using SafeMath for uint256;

    uint256 private _period;
    uint256 private _epoch;

    /* ========== CONSTRUCTOR ========== */

    constructor(
        uint256 period,
        uint256 startTime,
        uint256 startEpoch
    ) StartTimeLock(startTime) {
        _period = period;
        _epoch = startEpoch;
    }

    /* ========== Modifier ========== */
    modifier checkEpoch {
        require(block.timestamp >= nextEpochPoint(), "Epoch: not allowed");
        _;
        _epoch = _epoch.add(1);
    }

    /* ========== VIEW FUNCTIONS ========== */

    function getCurrentEpoch() public view returns (uint256) {
        return _epoch;
    }

    function getPeriod() public view returns (uint256) {
        return _period;
    }

    function nextEpochPoint() public view returns (uint256) {
        return startTime.add(_epoch.mul(_period));
    }

    /* ========== GOVERNANCE ========== */
    function setPeriod(uint256 period) external onlyOperator {
        _period = period;
    }
}

