contracts/test/TokenFaucet.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity =0.7.6;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/SafeERC20.sol";
import "@openzeppelin/contracts/math/SafeMath.sol";
import "@openzeppelin/contracts/utils/Context.sol";

/**
    Contract to provide with token funds to the caller
 */
contract TokenFaucet is Context {
    using SafeMath for uint256;
    using SafeERC20 for IERC20;

    IERC20 public token0;
    IERC20 public token1;
    IERC20 public token2;
    uint256 public maxAmount;
    mapping(address => bool) public admins;

    constructor(
        IERC20 _token0,
        IERC20 _token1,
        IERC20 _token2,
        uint256 _maxAmount,
        address[] memory _admins
    ) {
        token0 = _token0;
        token1 = _token1;
        token2 = _token2;
        maxAmount = _maxAmount;

        for (uint256 i = 0; i < _admins.length; ++i) {
            admins[_admins[i]] = true;
        }
    }

    function refill() public {
        uint256 refillAmount = maxAmount;

        if (admins[_msgSender()]) {
            refillAmount *= 20;
        }

        uint256 currentAmount = token0.balanceOf(_msgSender());
        if (currentAmount < refillAmount) {
            token0.safeTransfer(_msgSender(), refillAmount.sub(currentAmount));
        }

        currentAmount = token1.balanceOf(_msgSender());
        if (currentAmount < refillAmount) {
            token1.safeTransfer(_msgSender(), refillAmount.sub(currentAmount));
        }

        currentAmount = token2.balanceOf(_msgSender());
        if (currentAmount < refillAmount) {
            token2.safeTransfer(_msgSender(), refillAmount.sub(currentAmount));
        }
    }
}

contracts/test/MockStdReference.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity =0.7.6;
pragma abicoder v2;

import "../interfaces/IStdReference.sol";

contract MockStdReference is IStdReference {
    uint256 testRate = 1e18;

    bytes32 encodedANT;
    bytes32 encodedBNB;
    bytes32 encodedBUSD;

    constructor() {
        encodedANT = keccak256(abi.encodePacked("ANT"));
        encodedBNB = keccak256(abi.encodePacked("BNB"));
        encodedBUSD = keccak256(abi.encodePacked("BUSD"));
    }

    /// Returns the price data for the given base/quote pair. Revert if not available.
    function getReferenceData(
        string memory _base,
        string memory /*_quote*/
    ) public view override returns (ReferenceData memory) {
        ReferenceData memory data;

        bytes32 encodedBase = keccak256(abi.encodePacked(_base));

        if (encodedBase == encodedANT) {
            data.rate = testRate;
            data.lastUpdatedBase = 0;
            data.lastUpdatedQuote = 0;
        } else if (encodedBase == encodedBNB) {
            data.rate = 300 * 1e18;
            data.lastUpdatedBase = 0;
            data.lastUpdatedQuote = 0;
        } else if (encodedBase == encodedBUSD) {
            data.rate = 1e18;
            data.lastUpdatedBase = 0;
            data.lastUpdatedQuote = 0;
        } else {
            data.rate = 1e18;
            data.lastUpdatedBase = 0;
            data.lastUpdatedQuote = 0;
        }

        return data;
    }

    /// Similar to getReferenceData, but with multiple base/quote pairs at once.
    function getReferenceDataBulk(string[] memory _bases, string[] memory _quotes)
        external
        view
        override
        returns (ReferenceData[] memory)
    {
        ReferenceData[] memory data = new ReferenceData[](_bases.length);

        for (uint256 i = 0; i < data.length; ++i) {
            data[i] = getReferenceData(_bases[i], _quotes[i]);
        }

        return data;
    }

    function setTestRate(uint256 rate) external {
        testRate = rate;
    }
}

contracts/core/PoolStakerV3WithRewards.s.content:
// SPDX-License-Identifier: MIT
pragma solidity =0.7.6;
pragma abicoder v2;

/**
    ANTBNB-LP token pool. LP tokens staked here will generate ANT Token rewards
    to the holder
 */

import "@uniswap/v3-core/contracts/interfaces/IUniswapV3Pool.sol";
import "@uniswap/v3-core/contracts/interfaces/IERC20Minimal.sol";
import "@uniswap/v3-staker/contracts/interfaces/IUniswapV3Staker.sol";

import "@openzeppelin/contracts/math/SafeMath.sol";

import "../access/OperatorAccessControl.sol";

abstract contract IPoolStakerV3WithRewards {
    function createIncentive(
        uint256 rewardAmount,
        uint256 startTime_,
        uint256 endTime_
    ) external virtual;

    function stakeToken(uint256 tokenId) external virtual;

    function unstakeToken(uint256 tokenId) external virtual;

    function claimReward(address to, uint256 tokenId) external virtual returns (uint256 reward);

    function exit(address to, uint256 tokenId) external virtual returns (uint256 reward);

    function getRewardInfo(uint256 tokenId) external virtual returns (uint256 reward);
}

contract PoolStakerV3WithRewards is OperatorAccessControl {
    using SafeMath for uint256;

    IUniswapV3Staker poolStaker;
    IUniswapV3Pool public pool;
    IERC20Minimal public rewardToken;
    address public refundee;
    uint256 public startTime;
    uint256 public endTime;

    uint256 rewardAmount;

    mapping(uint256 => address) private owners;

    constructor(
        IUniswapV3Staker poolStaker_,
        IUniswapV3Pool pool_,
        IERC20Minimal rewardToken_,
        address refundee_
    ) {
        poolStaker = poolStaker_;
        pool = pool_;
        rewardToken = rewardToken_;
        refundee = refundee_;
    }

    function createIncentive(
        uint256 rewardAmount_,
        uint256 startTime_,
        uint256 endTime_
    ) external {
        startTime = startTime_;
        endTime = endTime_;
        rewardAmount = rewardAmount_;

        IUniswapV3Staker.IncentiveKey memory key = _getIncentiveKey();
        rewardToken.approve(address(poolStaker), rewardAmount);
        poolStaker.createIncentive(key, rewardAmount);
